INSERT INTO `Stories` (`story_id`, `title`, `description`, `author_id`, `urlImg`, `link_forum`, `genres_id`, `create_at`) VALUES
(20, 'Cạm Bẫy Người', 'Cạm bẫy người\" là một tác phẩm phóng sự của nhà văn Vũ Trọng Phụng, ra đời năm 1933, nhằm vạch trần và tố cáo tệ nạn cờ bạc bịp lan tràn ở Hà Nội thời bấy giờ. Để thực hiện tác phẩm, Vũ Trọng Phụng đã trực tiếp thâm nhập các sòng bạc, mô tả chi tiết cách thức hoạt động của \"làng bịp\", chân dung của những kẻ cầm đầu và những bi kịch mà họ gây ra cho các gia đình \"tín đồ tôn giáo đỏ đen tiết cách thức hoạt động của \"làng bịp\", chân dung của những kẻ cầm đầu và những bi kịch mà họ gây ra cho các gia đình \"tín đồ tôn giáo đỏ đen ', 11, 'https://res.cloudinary.com/djr4f7ceu/image/upload/v1757504771/stories_covers/ogwdojbwe0glf4n0rffp.jpg', '', 1, '2025-09-10 11:46:12'),
(21, 'Cơm thầy cơm cô', '\"Cơm thầy cơm cô\" là một thiên phóng sự của Vũ Trọng Phụng mô tả thực trạng di dân từ nông thôn ra thành thị dưới thời Pháp thuộc. Tác phẩm phơi bày cuộc sống khổ cực, bị bóc lột, lạm dụng và tệ nạn xã hội mà những người lao động nghèo gặp phải khi tìm đến \"ánh sáng kinh thành\". Thiên phóng sự này được đánh giá là một bức tranh chân thực, sâu sắc về xã hội đương thời, thể hiện tài năng của \"ông vua phóng sự đất Bắc\". ', 10, 'https://res.cloudinary.com/djr4f7ceu/image/upload/v1757505108/stories_covers/r3dxz4bwg1ybvajyqc3u.jpg', '', 1, '2025-09-10 11:51:49'),
(22, 'Giông tố', 'Truyện \"Giông Tố\" của Vũ Trọng Phụng là bức tranh hiện thực phản ánh xã hội Việt Nam những năm 1930, với sự thối nát, giả tạo của tầng lớp thượng lưu và sự bế tắc của tầng lớp dưới đáy xã hội. Tác phẩm phơi bày sự suy đồi đạo đức, sự tha hóa của con người qua các nhân vật tiêu biểu như Nghị Hách, Tú Anh, Long, và các nhân vật ở tầng lớp thấp như dân nghèo, kỹ nữ, khi mà mỗi người đều là nạn nhân hoặc thủ phạm trong một xã hội đầy rẫy bất công. ', 10, 'https://res.cloudinary.com/djr4f7ceu/image/upload/v1757505224/stories_covers/arjju96myeemivjexsug.jpg', '', 1, '2025-09-10 11:53:44'),
(23, 'Kỹ Nghệ Lấy Tây', 'Tác phẩm ra đời năm 1934, phản ánh chân thực cuộc sống xã hội Việt Nam thời Pháp thuộc, nơi Nho giáo suy tàn và làn sóng văn minh phương Tây tràn vào tạo ra nhiều biến đổi văn hóa và xã hội. ', 9, 'https://res.cloudinary.com/djr4f7ceu/image/upload/v1758071874/stories_covers/lpobtjqolnlrikegvywx.jpg', '', 2, '2025-09-17 01:17:55'),
(24, 'Số đỏ', 'Số đỏ là một tiểu thuyết văn học của nhà văn Vũ Trọng Phụng (ông vua phóng sự đất Bắc) đăng trên Hà Nội báo từ số 40 ngày 7 tháng 10 năm 1936 và được in thành sách lần đầu vào năm 1938, với 20 chương. Tác phẩm xoay quanh nhân vật chính là Xuân - biệt danh là Xuân Tóc đỏ - từ chỗ là một kẻ bị coi là hạ lưu, bỗng chốc đã bước lên tầng lớp danh giá của xã hội nhờ trào lưu Âu hóa của giới tiểu tư sản Hà Nội khi đó', 9, 'https://res.cloudinary.com/djr4f7ceu/image/upload/v1758072444/stories_covers/jfb6mm7mfkyv6dm63kg1.jpg', '', 5, '2025-09-17 01:27:25'),
(25, 'Lều chõng', 'Lều chõng là một tiểu thuyết phóng sự của nhà văn Ngô Tất Tố, ra mắt độc giả lần đầu ngày 10 tháng 3 năm 1939 tại Hà Nội.', 10, 'https://res.cloudinary.com/djr4f7ceu/image/upload/v1759544568/stories_covers/k2sasaop7xzouifw60nx.jpg', '', 2, '2025-10-04 02:22:49'),
(26, 'Vỡ đê', 'Tác phẩm \"Vỡ đê\" là một tiểu thuyết xã hội của nhà văn Vũ Trọng Phụng, ra đời năm 1936, được sáng tác cùng thời với \"Số đỏ\" và \"Giông tố\". Tác phẩm phản ánh chân thực hiện thực xã hội Việt Nam dưới ách thực dân, phong kiến, phê phán chính sách thống trị của bọn quan lại và tầng lớp cường hào đã đẩy nông dân vào cảnh lầm than, nghèo khổ', 10, 'https://res.cloudinary.com/djr4f7ceu/image/upload/v1759544660/stories_covers/cynfyowcpvoxvtnateac.png', '', 2, '2025-10-04 02:24:20');

